INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'办公自动化', N'办公自动化（Office Automation，简称：OA）指通过计算机等电子设备和软件，基于数字化技术，从创建到处理乃至传播过程，均通过信息化手段实现。其中，其基本活动是原始数据的存储、电子业务等信息管理。随着数字化信息的发展，相关工作人员之间可实现信息共享、工作协同等，简化了办公流程，提升了工作效率。', N'信息化热词', N'11', N'数字化 实现 信息 通过 活动 随着 之间 传播 手段 处理 office 业务 ', null, null, null)
