INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'企业资源计划', N'企业资源计划（Enterprise resource planning，简称：ERP）也被称为企业资源规划，在1990年由美国高德纳咨询公司首次提出。企业资源计划最初是指应用软件（APP），并且逐渐被国际上的商业企业推广，逐渐发展为现代企业管理理论，作为企业工作流程再造的有力工具。ERP是一个创建在信息技术基础上的系统化管理思想，为企业决策层及员工提供决策运行手段的管理平台。', N'信息化热词', N'12', N'企业 资源 计划 管理 逐渐 erp enterprise 公司 手段 员工 提供 决策 ', null, null, null)
GO
GO
INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'二维码', N'二维码又被称为二维条码，它是由一维条码发展而来，其数据被化成二进制，然后将二进制数据储存在黑白矩形图案中，通过电子设备扫描即可查看。其中，一维码和二维码的区别是：一维条码仅在一个方向上（一般是水平方向）存储信息，其垂直方向的高度常为了便于阅读器读取信息，但二维条码在垂直和水平方向均记载着数据。二维码与一维码相比较，二维码多了“定位点”和“容错机制”。“定位点”是指二维码上的三个定位角点，使得扫描设备即使从不同方向进行扫描，也可快速读取出二维码信息；“容错机制”是指条码部分损坏或污染，其信息仍然能够被还原，并且不同类型的二维码有不一样的容错能力。', N'信息化热词', N'13', N'二维码 条码 信息 一维 容错 扫描 数据 方向 定位 机制 水平 垂直 ', null, null, null)
GO
GO
INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'交互式网络电视', N'IPTV是通过宽带网络作为电视信号来源的一种宽带电视，其原理是将电视节目信息通过网际协议（Internet Protocol, 简称：IP）传送给用户。IPTV服务也常被称为“三合一服务”、“三重服务”，因为IPTV一般需要使用网络，所以服务供应商通常会一并提供联网及IP电话等相关服务。
虽然IPTV使用了宽带网络及网际协议，但IPTV也可以不通过互联网进行传输，选择封闭式Intranet（内部网）进行传输。
', N'信息化热词', N'14', N'iptv 服务 宽带 网络 进行 网际协议 ip 传输 通过 电视节目 用户 使用 ', null, null, null)
