INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'可穿戴设备', N'可穿戴设备是一种可以穿戴在身上或装饰在人们的衣服、配件上的便携式硬件设备。它是通过软件进行数据交互、云端交互，从而实现广泛的应用，可穿戴设备给我们的生活带来了极大的便捷。', N'信息化热词', N'15', N'设备 穿戴 交互 进行 一种 硬件 它是 极大 来了 便捷 配件 实现 ', null, null, null)
