INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'智能家居', N'智能家居主要采用安全防范、网络通信、 自动控制等先进技术，基于住宅平台，集成了人们的家居生活有关的设施，从而构建智能的住宅设施与家庭事务结合的管理系统，全面提升了家居安全性、舒适性、艺术性和便利性，打造一个环保节能的居住环境。', N'信息化热词', N'16', N'住宅 环保 防范 家居 有关 管理 系统 舒适性 智能 家居生活 采用 升了 ', null, null, null)
