INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'车联网', N'车联网是通过采集车辆位置、速度和路线等信息构成的交互网络。车联网使用了摄像头、传感器、GPS、RFID等装置，车辆可以完成自身位置和状态信息的采集。例如，日常生活中常用的导航系统，就是所有车辆通过互联网将车辆行驶信息传输到中央处理器，进行信息采集和处理后，从而及时汇报实时路况，计算出不同车辆的最佳路线、安排信号灯周期。', N'信息化热词', N'17', N'车辆 信息 采集 位置 路线 联网 通过 构成 进行 状态 所有 处理 ', null, null, null)
