INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'零售O2O', N'O2O（Online To Offline，简称：O2O）最早由美国提出，是指将线下的商务机会与线上互联网结合，让互联网成为线下交易的平台。O2O的概念比较宽泛，同时涉及线上和线下', N'信息化热词', N'18', N'线下 o2o 线上 互联网 美国 同时 比较 最早 提出 成为 交易 涉及 ', null, null, null)
