INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'共享经济', N'共享经济建立在不同个体或组织之间，主要对商品、服务等进行有偿或无偿共享，例如，共享汽车、交换住宿等。共享经济通过弱化拥有权，达到强化使用权的目的使物质利用效率尽可能最大化。在共享经济下，目前较为广泛的共享模式是，生产制造可以专门用于“共享”的商品，而不是将闲置资源进行共享，这种共享模式本质上其实是“租赁经济”。一些共享企业为了快速占有市场，利用资本大量投放“共享”商品，反而造成了浪费，甚至产生一些社会问题。例如，大量的共享单车乱停乱放，造成道路拥堵，还被堆成了“共享单车坟墓”，造成了极大的浪费。', N'信息化热词', N'19', N'共享 经济 商品 浪费 模式 大量 一些 利用 单车 造成了 进行 例如 ', null, null, null)
GO
