INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'移动支付', N'移动支付是指通过移动设备付款的服务。目前较为主流的移动支付方式有以下五种类型：使用短信的转账支付、移动应用支付（APP）、移动账单付款、非接触型支付（NFC）和移动设备网络支付（WAP）。2019年1月24日，交通运输部新闻发言人吴春耕在举行的例行新闻发布会上表示，2019年交通运输部紧紧抓住最直接、最现实人民群众最关心的利益问题，共列出12项民生实事任务的清单，以推动交通运输改革发展成果惠及全体人民。例如，2019年高速公路人工收费车道实现手机移动支付全覆盖，包括260个地级市以上城市交通一卡通互联互通等。', N'信息化热词', N'20', N'移动 支付 2019年 设备 运输部 付款 交通 成果 高速公路 覆盖 使用 较为 ', null, null, null)
