INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'3D打印', N'3D打印是以数字模型文件为基础，采用粉末状金属、塑料等可粘合材料作为原始打印材料，通过逐层打印来构造物体的快速成型技术。3D打印常使用数字技术材料打印机，被应用于模具制造、工业设计、工程和施工（AEC）、航空航天、土木工程、牙科和医疗产业、地理信息系统等领域，现在也可被用于产品的直接生产，例如：打印零部件；打印生物实验材料，2019年1月14日，美国加州大学圣迭戈分校首次利用快速3D打印技术，制造出模仿中枢神经系统结构的脊髓支架，成功帮助大鼠恢复了运动功能。', N'信息化热词', N'24', N'打印 材料 3d 技术 快速 地理信息系统 使用 成功 物体 原始 应用于 领域 ', null, null, null)
