INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'分布式存储', N'分布式存储是一种通过网络利用企业中的机器上的磁盘空间，并将这些分散的存储资源构建成虚拟的存储设备，将数据分散存储在不同的机器上。', N'大数据', N'26', N'存储 机器 分散 磁盘空间 一种 网络 分布式 企业 数据 这些 利用 虚拟 ', null, null, null)
