INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'云计算', N'云计算（英语：cloud computing），是一种基于互联网的计算方式，通过这种方式，共享的软硬件资源和信息可以按需求提供给计算机各种终端和其他设备。', N'云计算', N'31', N'计算 方式 各种 一种 信息 这种 终端 其他 英语 提供给 cloud 共享 ', null, null, null)
