INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'云计算有哪些优势？', N'（1）	超大规模。“云”具有相当的规模，Google云计算已经拥有100多万台服务器，亚马逊、IBM、微软和Yahoo等公司的“云”均拥有几十万台服务器。“云”能赋予用户前所未有的计算能力。（2）	虚拟化。云计算支持用户在任意位置使用各种终端获取服务。所请求的资源来自“云”', N'云计算', N'32', N'计算 用户 拥有 服务器 万台 亚马逊 使用 各种 公司 位置 规模 赋予 ', null, null, null)
