INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'数据可视化', N'数据可视化是对数据的属性或者关系的研究，利用图或表等人们便于理解的图形化表示方法表示出来。其中，这种表现形式常常被定义为以某种概要形式抽提出来的信息，包括相应信息单位的各种属性和变量。', N'大数据', N'37', N'属性 信息 数据 表示 各种 这种 表现形式 包括 相应 单位 理解 利用 ', null, null, null)
