INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'为什么要进行数据预处理？', N'由于数据库中的数据极易受噪声、丢失数据和不一致数据的侵扰，因为数据库太大（一般在千兆字节乃至以上），并且大多来自于多个异构数据源。低质量的数据信息容易导致挖掘结果不佳，难以获取想要的价值信息。当前衍生出大量数据预处理技术。例如，数据清理技术，常用来去掉数据中的噪声，纠正数据不一致；数据集成技术，将多个源的数据合并成一致的数据，再对其进行存储。', N'大数据', N'38', N'数据 技术 噪声 多个 数据库 信息 不一致 字节 进行 预处理 大量 丢失 ', null, null, null)
GO
