INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'数据仓库', N'数据仓库是一种资讯系统的资料储存理论，它注重利用某些特殊资料储存方式，让数据更加有利于进行数据分析处理，从而获取有价值的信息并依此作决策。存储在数据仓库中的资料，一旦被存入就不再随时间而更动，并且存储的数据具有时间属性。例如，数据仓库中常含有大量的历史性资料，可通过特定分析方式，从其中发掘出价值信息。', N'大数据', N'40', N'资料 数据仓库 数据 分析 存储 信息 时间 储存 方式 进行 属性 有利于 ', null, null, null)
GO
