INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'数据仓库的作用', N'数据仓库主要应用于数据库已大量存在的场景下，为了方便后续的数据挖掘、方便决策需要而产生，它并不是传统意义上的的“大型数据库”。建设数据仓库的目的是为前端查询、分析奠定基础，因为数据有较大冗余，所以数据的存储要求也较高。', N'大数据', N'42', N'数据 数据仓库 数据库 方便 分析 大量 为了 意义上 建设 前端 决策 应用于 ', null, null, null)
