INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'感知中国', N'“感知中国”就是中国的物联网，是中国发展物联网的一种形象称呼。通过在物体上植入各种微型感应芯片使其智能化，通过无线网络来实现人和物体之间的“对话”，物体和物体之间的“交流”。物联网展示了生活中任何物品都可以变得“有感觉、有思想”的智能图景，被认为是世界下一次信息技术浪潮和新经济引擎。', N'物联网', N'44', N'物体 联网 中国 之间 通过 下一次 新经济 称呼 各种 一种 感觉 浪潮 ', null, null, null)
