INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'第五代通信技术有哪些优势', N'（1）	传输速率更快，5G设备的数据速率为10Gbps，是4G设备的百倍以上，那就意味着，下载一部3GB电影只需5秒左右；（2）	响应延迟更底，5G设备的数据传输延迟在1ms以下，而4G设备的数据传输延迟为50ms。（3）	更高的吞吐量，5G的数据吞吐量每平方公里为100万，大约是4 G的几十到上百倍。', N'物联网', N'47', N'设备 5g 延迟 百倍 速率 吞吐量 数据传输 数据 4g 秒左右 意味着 以上 ', null, null, null)
