INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'SaaS', N'SaaS是最为成熟、最出名，也是得到最广泛应用的一种云计算。它是一种软件分布模式，在这种模式下，应用软件安装在厂商或者服务供应商那里，用户可以通过某个网络来使用这些软件，通常使用的网络是互联网。', N'云计算', N'55', N'模式 使用 一种 网络 软件 用户 成熟 这种 计算 它是 广泛应用 厂商 ', null, null, null)
