INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'数据中心', N'数据中心也被称为服务器场，意味着安置计算机系统及相关部件的设施，例如，电信和储存系统。数据中心一般包括冗余和备用电源、冗余数据通信连接、环境控制（例如空调、灭火器）和各种安全设备。', N'云计算', N'56', N'系统 例如 冗余 数据中心 电信 各种 空调 备用 意味着 包括 安置 连接 ', null, null, null)
