INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'DDoS攻击防范', N'分布式拒绝服务攻击(DDoS)是目前黑客经常采用而难以防范的攻击手段。分布式拒绝服务攻击手段DDoS是通过网络进行的多对一的攻击方式。DDoS 攻击实例——目前最流行的DDoS 攻击手段SYN Flood 攻击。', N'物联网', N'58', N'攻击 ddos 手段 拒绝服务 分布式 目前 进行 黑客 防范 网络 经常 实例 ', null, null, null)
