INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'云计算的特征 ', N'互联网上的云计算服务特征和自然界的云、水循环具有一定的相似性，根据美国国家标准和技术研究院的定义，云计算服务主要具备以下几条特征：（1）	根据实际需要提供自助服务。（2）	数据访问不受时间、地域、网络设备等限制。（3）	可支持多人共同享用数据池。（4）	部署迅速，灵活度较高。（5）	数据操作可监控，服务可量化。一般认为还有如下特征：（1）	可通过虚拟化技术，快速部署资源或获得服务。（2）	减少用户终端的处理负担。（3）	降低了用户对于IT专业知识的依赖。', N'云计算', N'63', N'服务 特征 数据 用户 计算 部署 技术 根据 认为 互联 处理 几条 ', null, null, null)
