INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'云计算的供货商有哪些？', N'从全球范围来看，公有云厂商前五名分别是AWS、微软、谷歌、阿里云和IBM，阿里云也是唯一上榜的中国厂商。AWS在四个区域均排在第一，微软在三个区域排在第二，但在亚太地区被阿里云赶超，屈居第三，谷歌在亚太地区的名次也下滑了一位，腾讯云排亚太地区第五。在其他市场，IBM在North America和EMEA Region排第四，Salesforce排第五，而在Latin America（拉丁美洲）市场，Salesforce超越IBM成为第四。', N'云计算', N'64', N'亚太地区 ibm 阿里 微软 厂商 america salesforce 排在 第四 谷歌 第五 区域 ', null, null, null)
