INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'双机热备', N'双机热备因两机高可用在国内使用较多，特指基于高可用系统中的两台服务器的热备（或高可用），故得名双机热备，双机高可用按工作中的切换方式分为：主-备方式（Active-Standby方式）和双主机方式（Active-Active方式）。主-备方式即指的是一台服务器处于某种业务的激活状态（即Active状态），另一台服务器处于该业务的备用状态（即Standby状态)。而双主机方式即指两种不同业务分别在两台服务器上互为主备状态（即Active-Standby和Standby-Active状态）', N'云计算', N'65', N'方式 状态 服务器 业务 可用 双机 处于 两台 主机 active-standby 使用 standby ', null, null, null)
