INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'虚拟化', N'虚拟化（技术）或虚拟技术（Virtualization）是将计算机的各种实体资源（CPU、内存、磁盘空间、网络适配器等）虚拟化的一种资源管理技术，通过抽象、转换后，构建成可供分割、组合为一个或多个计算机配置环境。由此，打破实体结构间的不可切割的障碍，使用户可以比原本的配置更好的方式来应用这些计算机硬件资源。这些资源的新虚拟部分是不受现有资源的架设方式，地域或物理配置所限制。一般所指的虚拟化资源包括计算能力和数据存储。', N'云计算', N'66', N'虚拟 资源 技术 配置 这些 方式 实体 磁盘空间 用户 更好 可供 计算 ', null, null, null)
