INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'虚拟化与云计算之间的关系', N'云计算是一个理论概念，而不用于特指某种具体技术。虚拟化是一种具体技术，指把硬件资源虚拟化，具有隔离性、可扩展性、安全性、资源可充分利用等特点的技术。 	CPU虚拟化的技术：Intel的VT-x，AMD的AMD-V。 	虚拟化的软件：VMWare，Hyper-V，Xen，KVM，OpenVZ等目前云计算，大多是依赖虚拟化，通过把多台服务器实体虚拟化后，构成一个资源池，实现共同计算，共享资源。', N'云计算', N'71', N'虚拟 技术 资源 计算 一个 具体 构成 一种 硬件 hyper-v openvz 目前 ', null, null, null)
