INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'政务云', N'政务云（Government Cloud）是指通过云计算技术，统筹机房、计算、应用支撑、信息资源等，发挥云计算的虚拟化、高可靠性、高通用性、高可扩展性，使其数据处理快速、按需、弹性服务，为政府行业提供基础设施、支撑软件、应用系统、信息资源、运行保障和信息安全等综合服务平台。', N'云计算', N'73', N'计算 支撑 应用 信息资源 服务 综合 弹性 提供 机房 系统 政务 cloud ', null, null, null)
