INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'区块链', N'通常形容区块链为一个大账本，从技术层面上说，是指所有区块链在一起的一个链表结构，类似于C/C++中的单项链表数据结构。只是每个区块保存前一个区块的hash码值，所以从某个区块逆向上溯，最终都会找到创世区块。', N'区块链', N'79', N'区块 一个 链表 c++ 最终 所有 在一起 单项 通常 只是 类似于 保存 ', null, null, null)
GO
