INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'比特币', N'比特币（BitCoin）最早在2009年被中本聪提出，最初设想是发布开源软件，并在其上构建P2P网络。比特币属于P2P形式的数字货币。点对点的传输目的是构建去中心化的支付系统。', N'区块链', N'92', N'比特 p2p 构建 设想 在其 网络 支付 提出 形式 数字 货币 点对点 ', null, null, null)
