INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'互联网时代敲诈者-勒索病毒', N'勒索病毒是一种新型电脑病毒，是黑客通过锁屏、加密文件等方式劫持用户文件并以此敲诈用户钱财的恶意软件。黑客通过网络钓鱼或利用系统漏洞等方式，向受害电脑或服务器植入病毒，从而加密硬盘上的文档甚至整个硬盘，之后向受害者索要数额不等的赎金后才给予解密。', N'智慧城市', N'99', N'用户 文件 黑客 病毒 加密 方式 通过 给予 钱财 一种 系统漏洞 后向 ', null, null, null)
