INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'网络监听的概念及原理', N'网络监听工具是利用计算机的网络接口截获目的地为其他计算机的数据报文的一种工具。可以作为能够捕捉网络报文的设备，也可以被理解为一种安装在计算机上的监听设备。可以用于监听计算机在网络上所产生的多种信息。 ', N'智慧城市', N'100', N'网络 可以 监听 计算机 一种 报文 设备 工具 截获 计算 能够 产生 ', null, null, null)
