INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'智慧城市的建设意义', N'（1）	建设智慧城市是实现城市可持续发展的需要（2）	建设智慧城市是信息技术发展的需要（3）	提高我国综合竞争力的战略选择', N'智慧城市', N'104', N'城市 智慧 建设 需要 我国 战略 综合 信息 提高 技术发展 竞争力 可持续发展 ', null, null, null)
