INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'什么是关键信息基础设施？', N'关键信息基础设施是面向公众提供网络信息服务或支撑金融、能源、交通、通信等重要行业运行的信息系统或工业控制系统。关键信息基础设施保护工作直接关系到国家安全、国计民生和公共利益', N'工业互联网', N'105', N'信息 关键 基础设施 重要 提供 关系到 支撑 金融 通信 国计民生 工作 行业 ', null, null, null)
GO
