INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'信息安全的主要表现形式', N'信息安全的主要表现形式有蠕虫或病毒扩散、垃圾邮件泛滥、黑客行为、信息系统脆弱性和有害信息的恶意传播等。', N'工业互联网', N'113', N'有害信息 垃圾邮件 黑客 病毒 信息 传播 蠕虫 泛滥 表现形式 主要 脆弱性 安全 ', null, null, null)
