INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'等级保护测评相关标准', N'相关标准主要有：《信息系统安全等级保护基本要求》《信息系统安全等级保护测评要求》《信息系统安全等级保护测评过程指南》', N'信息安全', N'121', N'等级 信息 系统安全 保护 测评 要求 基本要求 标准 相关 主要有 过程 指南 ', null, null, null)
