INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'什么是非对称加密？', N'非对称加密算法需要两个密钥：公开密钥（publickey）和私有密钥（privatekey）。公开密钥与私有密钥是一对，一个用于加密，另一个用来解密。如果用公开密钥对数据进行加密，只有用对应的私有密钥才能解密；如果用私有密钥对数据进行加密，那么只有用对应的公开密钥才能解密。因为加密和解密使用的是两个不同的密钥，所以这种算法叫作非对称加密算法。', N'信息安全', N'128', N'密钥 公开密钥 私有 加密 解密 进行 两个 如果 才能 对应 数据 加密算法 ', null, null, null)
