INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'什么是数字签名？', N'数字签名是通过某种密码运算生成一系列符号及代码组成电子密码进行签名，是只有信息发送者才能产生的别人无法伪造的一段数字串，这段数字串同时也有效证明了信息发送者发送信息的真实性的一个有效证明。"数字签名"是目前电子商务、电子政务中应用最普遍、可操作性最强、技术最成熟的一种电子签名方法。它采用规范化的程序和科学化的方法，用于鉴定签名人的身份、对电子数据内容的认可、验证文件的原文在传输过程中有无变动等，确保传输电子文件的真实性、完整性和不可抵赖性。', N'信息安全', N'129', N'电子 信息 数字串 方法 签名 文件 有效 真实性 密码 数字签名 发送者 传输 ', null, null, null)
