INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'病毒', N'计算机病毒是指编制或在计算机程序中插入的破坏计算机功能或者损坏数据、影响计算机使用、能自我复制的一组计算机指令或者程序代码。通俗的讲，计算机病毒是人为编写的有害代码。', N'计算机病毒的防范', N'130', N'计算机 或者 计算机病毒 复制 使用 程序代码 有害 人为 程序 通俗 编写 一组 ', null, null, null)
