INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'国内外知名的反病毒软件有哪些？', N'国内外常用反病毒软件有：瑞星杀毒；软件金山毒霸；诺顿（反病毒程序）；360杀毒（整合了五大领先查杀引擎）；腾讯电脑管家（拥有云查杀木马，系统加速，漏洞修复实时防护，网速保护，文档保护等功能）。', N'计算机病毒的防范', N'131', N'杀毒 保护 查杀 拥有 木马 系统 瑞星 领先 引擎 实时 诺顿 修复 ', null, null, null)
