INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'勒索病毒-事前防范', N'目前已知的是，Windows 10操作系统只要打开了自动更新，就不会有中毒的风险。而目前国内大量使用的Windows 7甚至Windows XP电脑则比较容易中毒。目前，微软已经为所有的Windows系统紧急发布了系统补丁。另外，像445这样的高危端口，一般的家用电脑也最好关闭掉。关闭方法为：（1）	打开“控制面板”，点击防火墙（2）	点击“高级设置”（3）	先点击“入站规则”，再点击“新建规则”（4）	勾选“端口”，点击“协议与端口”（5）	勾选“特定本地端口”，填写445，点击下一步（6）	点击“阻止链接”，一直下一步，即可完成', N'计算机病毒的防范', N'141', N'点击 端口 windows 目前 下一步 445 规则 系统 中毒 关闭 打开 防火墙 ', null, null, null)
GO
GO
INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'勒索病毒-中毒后应对方法', N'（1）	立即组织内网检测，查找所有开放445 SMB服务端口的终端和服务器，一旦发现中毒机器，立即断网处理。目前看来对硬盘格式化可清除病毒。对于XP、2003等微软已不再提供安全更新的机器，建议升级操作系统版本或使用360“NSA武器库免疫工具”对系统进行检测，查看是否存在漏洞，并关闭受到漏洞影响的端口，以此避免遭到勒索软件等病毒的侵害。（2）	一旦发现电脑中毒，立即断网。启用并打开“Windows防火墙”，进入“高级设置”，在入站规则里禁用“文件和打印机共享”相关规则。关闭UDP135、445、137、138、139端口，关闭网络文件共享。尽快对电脑中的重要文件资料进行备份。（3）	严格禁止使用U盘、移动硬盘等可执行摆渡攻击的设备。及时更新操作系统和应用程序到最新的版本。', N'计算机病毒的防范', N'142', N'立即 关闭 端口 使用 检测 病毒 更新 445 操作系统 进行 文件 规则 ', null, null, null)
GO
GO
INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'计算机病毒有利的一面', N'计算机病毒对抗是电子战的新领域，也是近年来C3I对抗中的热门话题。目前世界各国的军事电子领域都竞相开始研究这种全新的电子战“武器”，计算机病毒所攻击的是系统的核心部件，此外，它还具有隐蔽性和传染性的特点，这就为电子对抗提供了崭新的技术手段。科学家断言：未来战争破坏力最大的已不再是核打击，在电脑已经成为军事指挥、武器控制和国家经济中枢的情况下，“计算机病毒打击”将更直接、更危险。可想而知，随着微电脑技术在军事领域越来越广泛地应用，进攻性的计算机病毒武器将迅速投入战场。计算机病毒武器将成为信息战的杀手锏。黑客与入侵检测', N'计算机病毒的防范', N'143', N'计算机病毒 武器 军事 领域 对抗 电子战 核心部件 随着 打击 广泛地 这种 检测 ', null, null, null)
