INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'什么是黑客？', N'黑客，是Hacker的译音，源于Hack，本意为“做了一件非常漂亮的工作”，原指一群精力旺盛、专业技能超群、聪明能干、对计算机信息系统进行非授权访问的人员。而现在黑客泛指在计算机技术上有一定特长，掌握一定的技术知识，采用非法手段逃过计算机网络系统的访问控制，而未经授权或非法访问进入计算机网络的人，被认为是网络“攻击者”和“破坏者”。', N'计算机病毒的防范', N'144', N'访问 一定 黑客 授权 计算机 进行 原指 破坏者 意为 非常 精力旺盛 专业技能 ', null, null, null)
GO
