INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'黑客攻击的分类', N'（1）	攻击的行为主动性分为：主动攻击、被动攻击。（2）	按攻击的位置情况可分为：远程攻击、本地攻击、伪远程攻击。', N'计算机病毒的防范', N'146', N'攻击 远程 可分为 本地 主动性 位置 情况 被动 行为 分为 主动攻击 ', null, null, null)
