INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'黑客攻击的过程', N'黑客常用的攻击步骤虽然变化万千，但其整个攻击过程也具有一定规律，一般可以分为五步：隐藏IP、踩点扫描、获得特权、种植后门、隐身退出。黑客根据实际情况变化可能会对攻击步骤进行调整，掌握黑客的攻击规律有利于进行有针对性的防范。网络情况复杂，黑客个性迥异，其攻击步骤也各有不同，这五个攻击步骤是对一般情况而言的，是绝大部分黑客在通常情况下的一般规律。', N'计算机病毒的防范', N'147', N'攻击 黑客 步骤 规律 一般 进行 变化 调整 有利于 防范 绝大部分 扫描 ', null, null, null)
