INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'DDoS攻击防范', N'分布式拒绝服务攻击(Distributed？Denial？of？Service，简称DDoS)是一种目前黑客经常采用但又难以防范的攻击手段。分布式拒绝服务攻击手段是通过网络进行的多对一的攻击方式。被攻击的主机会出现大量等待的TCP连接，网络中充斥着大量无用的数据包，造成网络拥塞，使受害主机无法正常与外界通讯，严重时会造成系统死机。因为DDoS是利用TCP/IP协议的漏洞，所以对于DDoS的防御还是比较困难的。目前，对于DDoS的主要防范策略有尽早发现系统存在的攻击漏洞，及时安装系统补丁程序；经常检查系统的物理环境，禁止不必要的网络服务；利用网络设备如防火墙等加固网络的安全性等方法。', N'计算机病毒的防范', N'155', N'攻击 网络 ddos 系统 防范 大量 目前 利用 对于 经常 手段 分布式 ', null, null, null)
