INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'黑客', N'黑客(Hacker)源于Hack，本意为“干了一件非常 漂亮的事”，原指一群专业技能超群、聪明能干、 精力旺盛、对计算机信息系统进行百授权访问的 人员。 现通常将泛指的黑客认为是在计算机技术上有一 定特长，并凭借掌握的技术知识，采用非法手段 逃过计算机网络系统的访问控制，而进入计算机 网络进行未授权或非法访问的人。认为是网络 “攻击者”和“破坏者”。', N'黑客与入侵检测', N'156', N'访问 计算机 进行 认为是 黑客 网络 授权 泛指 原指 破坏者 常将 意为 ', null, null, null)
