INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'防范攻击的措施与步骤', N'防范攻击的措施有以下几种：（1）	使用防火墙和防毒软件（2）	定期分析系统日志（3）	安全管理检测（4）	做好数据备份（5）	设置安全口令（6）	进行动态站点监控（7）	完善服务器系统安全性能（8）	实施存取控制加密及认证（9）	提高防范意识防范攻击的步骤如下：（1）	选择操作系统升级补丁（2）	关闭无用的端口和服务（3）	隐藏IP地址（4）	查找本机漏洞（5）	选择防火墙软件', N'黑客与入侵检测', N'159', N'防范 防火墙 选择 攻击 安全 本机 使用 提高 检测 管理 认证 站点 ', null, null, null)
