INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'入侵检测系统的概念与功能', N'入侵检测系统（Intrusion Detection System，IDS）是进行入侵检测监控和分析过程自动化的软件与硬件的组合系统。它处于防火墙之后对网络活动进行实时检测。入侵检测技术IDS是一种主动保护自己免受攻击的一种网络安全技术。入侵检测系统的功能主要有：（1）	系统构造和弱点的审计（2）	异常行为模式的统计分析（3）	识别反映已知进攻的活动模式并向相关人员报警（4）	操作系统的审计跟踪管理，并识别用户违反安全策略的行为（5）	监视、分析用户及系统活动（6）	评估重要系统和数据文件的完整性', N'黑客与入侵检测', N'160', N'检测 系统 入侵 用户 模式 技术 审计 识别 ids 进行 分析 活动 ', null, null, null)
GO
