INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'网络监听的检测', N'网络监听的威胁网络监听能够捕获密码，这大概是绝大多数人非法使用监听工具的理由 。网络监听还能够捕获专用的或者机密的信息。网络监听的检测方法在Linux 下对嗅探攻击的程序检测方法比较简单，一般只要检查网卡是否处于混杂模式就可以了；而在Windows 平台中，并没有现成的函数可供实现这个功能，只要可以执行程序检查一下是否有嗅探程序在运行即可。(c:\windows\Drwatson.exe) ', N'黑客与入侵检测', N'164', N'监听 网络 程序 检测 能够 捕获 是否 只要 windows 方法 机密 处于 ', null, null, null)
