INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'缓冲区溢出攻防', N'缓冲区溢出的原理缓冲区溢出攻击是指通过往程序的缓冲区写超出其长度的内容，造成缓冲区的溢出，从而破坏程序的堆栈，使程序转而执行其他指令，以达到攻击的目的。造成缓冲区溢出的原因是没有仔细检查程序中用户输入的参数。最常见的缓冲区溢出攻击手段是通过制造缓冲区溢出使程序运行一个用户shell，再通过shell 执行其他命令。如果该程序属于root 且有suid 权限，攻击者就获得了一个有root 权限的shell，可以对系统进行任意操作。', N'黑客与入侵检测', N'166', N'缓冲区 溢出 程序 攻击 shell 用户 执行 root 权限 造成 一个 其他 ', null, null, null)
GO
