INSERT INTO [dbo].[words] ([name], [expl], [type], [id], [keyword], [webname], [webaddr], [val]) VALUES (N'防范攻击的措施与步骤', N'防范攻击的措施1.  提高防范意识2.  设置安全口令3.  实施存取控制4.  加密及认证5.  定期分析系统日志6.  完善服务器系统安全性能7.  进行动态站点监控8.  安全管理检测9.  做好数据备份10. 使用防火墙和防毒软件防范攻击的步骤1.  选好操作系统2.  补丁升级3.  关闭无用的服务和端口4.  隐藏IP 地址5.  查找本机漏洞6.  选用防火墙软件', N'黑客与入侵检测', N'168', N'防范 防火墙 5. 1. 攻击 6. 2. 3. 安全 4. 本机 使用 ', null, null, null)
